    public static int factorial(int n) {
    if (n <= 2){
        return n;
    }
    return n * factorial(n - 1);
    }

    
    public static int combination (int n, int m){
        int comb;
        return comb = factorial(n)/(factorial(n-m)*factorial(m)); //combination of n taken m = n! / (m! (n-m)!)
        
    }
    
