    private void btnOk1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnOk1ActionPerformed
        int n, m;
        
        n = Integer.parseInt(txtField.getText());
        m = Integer.parseInt(txtField1.getText());
        
        if(m >= n)
            txtPane.setText("Invalid Input");
        else{
            System.out.println("DEBUG--FACTORIAL");
            System.out.println(factorial(n));
            System.out.println(factorial(m));
            txtPane.setText(Integer.toString(combination(n, m)));
        
            System.out.println("\nDEBUG--COMBINATION");
            System.out.println(combination(n, m));
        }

        
        
        
        
    }//GEN-LAST:event_btnOk1ActionPerformed

